import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';


export default function Marks() {
  const [allMarks, setAllMarks] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [markById, setMarkById] = useState({});
  const [newMark, setNewMark] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_date: '',
  });
  const [updateMarkData, setUpdateMarkData] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_date: '',
  });
  const [deleteStudentId, setDeleteStudentId] = useState('');

  useEffect(() => {
    getAllMarks();
  }, []);

  const getAllMarks = () => {
    axios.get('http://localhost:3001/marks')
      .then(response => {
        setAllMarks(response.data);
      })
      .catch(error => console.error('Error fetching marks:', error));
  };

  const getMarkById = async () => {
    try {
      const response = await axios.get(`/marks/${studentId}`);
      setMarkById(response.data);
    } catch (error) {
      console.error('Error fetching mark by ID:', error);
    }
  };

  const addNewMark = () => {
    axios.post('http://localhost:3001/marks', newMark)
      .then(response => {
        getAllMarks();
        setNewMark({
          student_id: '',
          telugu: '',
          english: '',
          science: '',
          maths: '',
          social: '',
          exam_date: '',
        });
        toast.success('Mark added successfully!',
          {
            style: {
              backgroundColor: 'white',
              color: 'black',
            },
          });
      })
      .catch(error => {
        console.error('Error adding mark:', error);
        toast.error('Error adding mark');
      });
  };

  const updateMark = async () => {
    try {
      await axios.put(`/marks/${updateMarkData.student_id}`, updateMarkData);
      getAllMarks();
      toast.success('Mark updated successfully!',
        {
          style: {
            backgroundColor: 'white',
            color: 'black',
          },
        });
    } catch (error) {
      console.error('Error updating mark:', error);
      toast.error('Error updating mark');
    }
  };

  const deleteMark = async () => {
    try {
      await axios.delete(`/marks/${deleteStudentId}`);
      getAllMarks();
      toast.success('Mark deleted successfully!',
        {
          style: {
            backgroundColor: 'white',
            color: 'black',
          },
        });
    } catch (error) {
      console.error('Error deleting mark:', error);
      toast.error('Error deleting mark');
    }
  };

  return (
    <>
      <ToastContainer autoClose={3000} />

      <div className="main-container">
        <div className="fetch-data-container">
          <div className="fetch-data-form buttons-container">
            <h2>Fetch Mark Data</h2>
            <input
              type="text"
              placeholder="Enter Student ID"
              value={studentId}
              onChange={(e) => setStudentId(e.target.value)}
            />
            <button onClick={getMarkById}>Fetch Data</button>
          </div>

          {markById ? (
            <div className="fetched-mark-container">
              <h3>Fetched Mark Data</h3>
              <p>Student ID: {markById.student_id}</p>
              <p>Telugu: {markById.telugu}</p>
              <p>English: {markById.english}</p>
              <p>Science: {markById.science}</p>
              <p>Maths: {markById.maths}</p>
              <p>Social: {markById.social}</p>
              <p>Exam Date: {markById.exam_date}</p>
            </div>
          ) : null}
        </div>

        <div className="marks-table-container">
          <h2>All Marks</h2>
          <table>
            <thead>
              <tr>
                <th>Student ID</th>
                <th>Telugu</th>
                <th>English</th>
                <th>Science</th>
                <th>Maths</th>
                <th>Social</th>
                <th>Exam Date</th>
              </tr>
            </thead>
            <tbody>
              {allMarks.map(mark => (
                <tr key={mark.student_id}>
                  <td>{mark.student_id}</td>
                  <td>{mark.telugu}</td>
                  <td>{mark.english}</td>
                  <td>{mark.science}</td>
                  <td>{mark.maths}</td>
                  <td>{mark.social}</td>
                  <td>{mark.exam_date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="add-update-delete-container">
          <h2>Add / Update / Delete Mark</h2>
          <div className="add-update-delete-form">
            <h3>Add New Mark</h3>
            {/* Include input fields for new mark details */}
            {/* ... */}
            <button onClick={addNewMark}>Add Mark</button>

            <h3>Update Mark</h3>
            {/* Include input fields for updated mark details */}
            {/* ... */}
            <button onClick={updateMark}>Update Mark</button>

            <h3>Delete Mark</h3>
            <input
              type="text"
              placeholder="Enter Student ID"
              value={deleteStudentId}
              onChange={(e) => setDeleteStudentId(e.target.value)}
            />
            <button onClick={deleteMark}>Delete Mark</button>
          </div>
        </div>
      </div>
    </>
  );
}
