import React, { useState } from 'react';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';

export default function Home() {
  const [allMarks, setAllMarks] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [markById, setMarkById] = useState(null);
  const [newMark, setNewMark] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_date: '',
  });
  const [updateMarkData, setUpdateMarkData] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_date: '',
  });

const [showNoDataMessage, setShowNoDataMessage] = useState(false);
const [isUpdateMode, setIsUpdateMode] = useState(false);
const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);

//fetchall---------------------------------------------------
  const fetchallMarks =  () => {
    axios.get('http://localhost:3001/marks')
    .then(response => {
      setAllMarks(response.data);
    })
    .catch(error => console.error('Error fetching donors:', error));
  };
//fetchbyid------------------------------------------------
  const getMarkById = () => {
    axios.get(`http://localhost:3001/marks/${studentId}`)
      .then(response => {
        setMarkById(response.data);
        setShowNoDataMessage(false);
      })
      .catch(error => {
        console.error('Error fetching donor:', error);
        setMarkById(null);
        setShowNoDataMessage(true);
      });
  };

//insering marks--------------------------------
  const addNewMark = () => {
    axios.post('http://localhost:3001/marks', newMark)
      .then(response => {
        fetchallMarks();
        setNewMark({ student_id: '', telugu: '', english: '', science: '', maths: '' , social: '', exam_date: '' });
        toast.success(' added successfully!',
        {
          style: {
            backgroundColor: 'white', // Set your desired background color (black in this case)
            color: 'black', // Set the text color
          },
        });
      })
      .catch(error =>
        {console.error('Error adding donor:', error);
      toast.error('Error adding donation');} );
  };
//update mark--------------------------------------
const updateMark = async () => {
  try {
    await axios.put(`http://localhost:3001/marks/${updateMarkData.student_id}`, updateMarkData);
    fetchallMarks(); // Refresh the marks list after updating a mark
    toast.success('Mark updated successfully!',
    {
      style: {
        backgroundColor: 'white', // Set your desired background color (black in this case)
        color: 'black', // Set the text color
      },
      onClose: () => {
        // Set isUpdateMode to false when the toast is closed
        setIsUpdateMode(false);
      },
    });
  } catch (error) {
    console.error('Error updating mark:', error);
    toast.error('Error updating mark');
  }
};
//delete mark---------------------------------------

  const deleteMark = async () => {
    axios.delete(`http://localhost:3001/marks/${studentId}`)
    .then(response => {
      console.log(response.data);
      fetchallMarks(); // Refresh the donors list
    })
    .catch(error => console.error('Error deleting donor:', error));
  };
//------------------------------------------
  return (
    <>
    <ToastContainer autoClose={3000} />
      <div className="responsive-container-block bigContainer">
      </div>

      <h2>Student Marks CRUD Operations</h2>

      <button onClick={fetchallMarks}>Get All Marks</button>
      <div>
         {/* Display data in a table */}
         <table>
          <thead>
            <tr>
              <th>Student ID</th>
              <th>Telugu</th>
              <th>English</th>
              <th>Science</th>
              <th>Maths</th>
              <th>Social</th>
              <th>Exam Date</th>
            </tr>
          </thead>
          <tbody>
            {allMarks.map((data,index) => (
              <tr key={index}>
                <td>{data[0]}</td>
                <td>{data[1]}</td>
                <td>{data[2]}</td>
                <td>{data[3]}</td>
                <td>{data[4]}</td>
                <td>{data[5]}</td>
                <td>{data[6]}</td>
                
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    <br></br>
      <label htmlFor="studentId">Student ID:</label>
      <input
        type="text"
        id="studentId"
        value={studentId}
        onChange={(e) => setStudentId(e.target.value)}
      />
      <button onClick={getMarkById}>Get Mark by ID</button>
      {markById ? (
      <div className="fetched-donor-container">
        <h3>Fetched Student Data</h3>
        <p>Student ID: {markById[0]}</p>
        <p>Telugu: {markById[1]}</p>
        <p>English: {markById[2]}</p>
        <p>Science: {markById[3]}</p>
        <p>Maths: {markById[4]}</p>
        <p>Social: {markById[5]}</p>
        <p>Exam Date: {markById[6]}</p>

        <div >
          <button onClick={() => {setIsUpdateMode(true);getMarkById();}}>Update</button>
          {isUpdateMode && (
  <>
<h3>Update Mark</h3>
      <label htmlFor="updateStudentId">Student ID:</label>
      <input
        type="text"
        id="updateStudentId"
        value={updateMarkData.student_id}
        onChange={(e) => setUpdateMarkData({ ...updateMarkData, student_id: e.target.value })}
      />
       <label htmlFor="updateTelugu">Telugu:</label>
       <input
  type="text"
  id="updateTelugu"
  value={updateMarkData.telugu}
  onChange={(e) => setUpdateMarkData({ ...updateMarkData, telugu: e.target.value })}
/>

<label htmlFor="updateEnglish">English:</label>
<input
  type="text"
  id="updateEnglish"
  value={updateMarkData.english}
  onChange={(e) => setUpdateMarkData({ ...updateMarkData, english: e.target.value })}
/>

<label htmlFor="updateScience">Science:</label>
<input
  type="text"
  id="updateScience"
  value={updateMarkData.science}
  onChange={(e) => setUpdateMarkData({ ...updateMarkData, science: e.target.value })}
/>

<label htmlFor="updateMaths">Maths:</label>
<input
  type="text"
  id="updateMaths"
  value={updateMarkData.maths}
  onChange={(e) => setUpdateMarkData({ ...updateMarkData, maths: e.target.value })}
/>

<label htmlFor="updateSocial">Social:</label>
<input
  type="text"
  id="updateSocial"
  value={updateMarkData.social}
  onChange={(e) => setUpdateMarkData({ ...updateMarkData, social: e.target.value })}
/>

<label htmlFor="updateExamDate">Exam Date:</label>
<input
  type="text"
  id="updateExamDate"
  value={updateMarkData.exam_date}
  onChange={(e) => setUpdateMarkData({ ...updateMarkData, exam_date: e.target.value })}
/>

      <button onClick={updateMark}>Update Mark</button>
</>
      )}
          <button onClick={() => deleteMark(markById[0])}>Delete</button>
        </div>
      </div>
    ) : showNoDataMessage ? (
      <p className="no-data-message">No data available for the specified ID</p>
    ) : null}
      
      <h3>Add New Mark</h3>
      <input
        type="text"
        placeholder="ID"
        value={newMark.student_id}
        onChange={(e) => setNewMark({ ...newMark, student_id: e.target.value })}
      />
      <input
        type="text"
        placeholder="ID"
        value={newMark.telugu}
        onChange={(e) => setNewMark({ ...newMark, telugu: e.target.value })}
      />
      <input
        type="text"
        placeholder="ID"
        value={newMark.english}
        onChange={(e) => setNewMark({ ...newMark, english: e.target.value })}
      />
      <input
        type="text"
        placeholder="ID"
        value={newMark.science}
        onChange={(e) => setNewMark({ ...newMark, science: e.target.value })}
      />
        <input
        type="text"
        placeholder="ID"
        value={newMark.maths}
        onChange={(e) => setNewMark({ ...newMark, maths: e.target.value })}
      />
        <input
        type="text"
        placeholder="ID"
        value={newMark.social}
        onChange={(e) => setNewMark({ ...newMark, social: e.target.value })}
      />
        <input
        type="text"
        placeholder="ID"
        value={newMark.exam_date}
        onChange={(e) => setNewMark({ ...newMark, exam_date: e.target.value })}
      />
      <button onClick={addNewMark}>Add New Mark</button>

     
      

      
    </>
  );
