import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';

import { ToastContainer, toast } from 'react-toastify';

export default function Home() {
  const [allMarks, setAllMarks] = useState([]);
  const [studentId, setStudentId] = useState('');
  const [markById, setMarkById] = useState({});
  const [newMark, setNewMark] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_date: '',
  });
  const [updateMarkData, setUpdateMarkData] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_date: '',
  });
  const [deleteStudentId, setDeleteStudentId] = useState('');

  useEffect(() => {
    getAllMarks();
  }, []);

  const getAllMarks = () => {
    axios.get('http://localhost:3001/a')
    .then(response => {
      setDonors(response.data);
    })
    .catch(error => console.error('Error fetching donors:', error));
  };

  const getMarkById = async () => {
    try {
      const response = await axios.get(`/marks/${studentId}`);
      setMarkById(response.data);
    } catch (error) {
      console.error(error);
    }
  };


  const addNewMark = () => {
    axios.post('http://localhost:3001/marks', newMark)
      .then(response => {
        getAllMarks();
        setNewMark({ student_id: '', telugu: '', english: '', science: '', maths: '' , social: '', exam_date: '' });
        toast.success(' added successfully!',
        {
          style: {
            backgroundColor: 'white', // Set your desired background color (black in this case)
            color: 'black', // Set the text color
          },
        });
      })
      .catch(error =>
        {console.error('Error adding donor:', error);
      toast.error('Error adding donation');} );
  };

  const updateMark = async () => {
    try {
      await axios.put(`/marks/${updateMarkData.student_id}`, updateMarkData);
      getAllMarks(); // Refresh the marks list after updating a mark
    } catch (error) {
      console.error(error);
    }
  };

  const deleteMark = async () => {
    try {
      await axios.delete(`/marks/${deleteStudentId}`);
      getAllMarks(); // Refresh the marks list after deleting a mark
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
    <ToastContainer autoClose={3000} />
      <div className="responsive-container-block bigContainer">
        {/* Include your existing JSX code here */}
      </div>

      <h2>Student Marks CRUD Operations</h2>

      <button onClick={getAllMarks}>Get All Marks</button>
      <div>
        <pre>{JSON.stringify(allMarks, null, 2)}</pre>
      </div>

      <label htmlFor="studentId">Student ID:</label>
      <input
        type="text"
        id="studentId"
        value={studentId}
        onChange={(e) => setStudentId(e.target.value)}
      />
      <button onClick={getMarkById}>Get Mark by ID</button>
      <div>
        <pre>{JSON.stringify(markById, null, 2)}</pre>
      </div>

      <h3>Add New Mark</h3>
      <input
        type="text"
        placeholder="ID"
        value={newMark.student_id}
        onChange={(e) => setNewMark({ ...newMark, student_id: e.target.value })}
      />
      <input
        type="text"
        placeholder="ID"
        value={newMark.telugu}
        onChange={(e) => setNewMark({ ...newMark, telugu: e.target.value })}
      />
      <input
        type="text"
        placeholder="ID"
        value={newMark.english}
        onChange={(e) => setNewMark({ ...newMark, english: e.target.value })}
      />
      <input
        type="text"
        placeholder="ID"
        value={newMark.science}
        onChange={(e) => setNewMark({ ...newMark, science: e.target.value })}
      />
        <input
        type="text"
        placeholder="ID"
        value={newMark.maths}
        onChange={(e) => setNewMark({ ...newMark, maths: e.target.value })}
      />
        <input
        type="text"
        placeholder="ID"
        value={newMark.social}
        onChange={(e) => setNewMark({ ...newMark, social: e.target.value })}
      />
        <input
        type="text"
        placeholder="ID"
        value={newMark.exam_date}
        onChange={(e) => setNewMark({ ...newMark, exam_date: e.target.value })}
      />
      {/* Include other input fields for new mark details */}
      <button onClick={addNewMark}>Add New Mark</button>

      <h3>Update Mark</h3>
      <label htmlFor="updateStudentId">Student ID:</label>
      <input
        type="text"
        id="updateStudentId"
        value={updateMarkData.student_id}
        onChange={(e) => setUpdateMarkData({ ...updateMarkData, student_id: e.target.value })}
      />
      {/* Include other input fields for updated mark details */}
      <button onClick={updateMark}>Update Mark</button>

      <h3>Delete Mark</h3>
      <label htmlFor="deleteStudentId">Student ID:</label>
      <input
        type="text"
        id="deleteStudentId"
        value={deleteStudentId}
        onChange={(e) => setDeleteStudentId(e.target.value)}
      />
      <button onClick={deleteMark}>Delete Mark</button>
      
    </>
  );
}
