import './App.css';
import React, { useState, useEffect } from 'react';
import Navbar from './Components/Navbar';
import Marks from './Components/Marks';
import Home from './Components/Home';
import About from './Components/About';
import Stumarks from './Components/Stumarks';
import Reg from './Components/Reg';
import Login from './Components/Login';
import Gallery from './Components/Gallery';
import FacultyHome from './Components/FacultyHome';
import StudentHome from './Components/StudentHome';
import ManagementHome from './Components/ManagementHome';
import NonTechnicalHome from './Components/NonTechnicalHome';
import './Components/sty.css';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';

const PrivateRoute = ({ children, role }) => {
  const userRole = localStorage.getItem('userRole');
  return userRole === role ? children : <Navigate to="/" />;
};

function App() {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const role = localStorage.getItem('userRole');
    setUserRole(role);
  }, []);

  return (
    <>
      <Navbar role={userRole} />
      <Routes>
        <Route path='/Home' element={<Home />} />
        <Route path='/About' element={<About />} />
        <Route path='/Marks' element={<Marks />} />
        <Route path='/Stumarks' element={<Stumarks />} />
        <Route path='/Login' element={<Login />} />
        <Route path='/Gallery' element={<Gallery />} />
        <Route path='/' element={<Login />} />
        <Route path='/Reg' element={<Reg />} />
        <Route path="/Navbar/*" element={<Navbar />} />

        {/* Role-specific routes */}
        <Route
          path="/faculty-home"
          element={
            <PrivateRoute role="faculty">
              <FacultyHome />
            </PrivateRoute>
          }
        />
        <Route
          path="/student-home"
          element={
            <PrivateRoute role="student">
              <StudentHome />
            </PrivateRoute>
          }
        />
        <Route
          path="/management-home"
          element={
            <PrivateRoute role="management">
              <ManagementHome />
            </PrivateRoute>
          }
        />
        <Route
          path="/non-technical-home"
          element={
            <PrivateRoute role="non-technical">
              <NonTechnicalHome />
            </PrivateRoute>
          }
        />

        {/* Redirect to Login for any unknown routes */}
        <Route path='*' element={<Navigate to="/Login" />} />
      </Routes>
    </>
  );
}

export default App;
