import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function Login() {
  const [loginDetails, setLoginDetails] = useState({
    email: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    // Perform login logic, then:
    const role = data.role; // Assume you get the role from the login response
    localStorage.setItem('userRole', role);
  
    if (role === 'faculty') {
      window.location.href = '/faculty-home';
    } else if (role === 'student') {
      window.location.href = '/student-home';
    } else if (role === 'management') {
      window.location.href = '/management-home';
    } else if (role === 'non-technical') {
      window.location.href = '/non-technical-home';
    } else {
      window.location.href = '/';
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginDetails(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <>
      <nav>
        <div className="logo">Edu Sphere</div>
        <input type="checkbox" id="click" />
        <label htmlFor="click" className="menu-btn">
          <i className="fas fa-bars"></i>
        </label>
        <ul>
          <li><Link to="/Reg">Register</Link></li>
        </ul>
      </nav>

      <br /><br />
      <h3 className='su2'>Sign In</h3> <br />
      <p className="hr-line"></p>

      <ToastContainer />

      <div className="login-page">
        <div className="login-container">
          <h2 className="login-title">Sign In</h2>
          <form autoComplete="off">
            <div className="input-box">
              <i className="fa-solid fa-envelope"></i>
              <input
                type="text"
                name="email"
                placeholder="Enter your email"
                value={loginDetails.email}
                onChange={handleInputChange}
              />
            </div>

            <div className="input-box">
              <i className="fa-solid fa-key"></i>
              <input
                type="password"
                name="password"
                placeholder="Enter your password"
                value={loginDetails.password}
                onChange={handleInputChange}
              />
            </div>

            <div className="button">
              <input type="button" value="Sign In" onClick={handleLogin} />
            </div>
          </form>
          <p className="signup-link">
            Don't have an account? <Link to="/Reg">Sign Up</Link>
          </p>
        </div>
      </div>
    </>
  );
}
