import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'Toastify.css';
import { FaEnvelope, FaLock } from 'react-icons/fa';


export default function Login() {
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };
  const [loginDetails, setLoginDetails] = useState({
    email: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: loginDetails.email,
          password: loginDetails.password,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Internal Server Error');
      }

      console.log('Login successful');

      // If login is successful, update isAuthenticated state
      setIsAuthenticated(true);

      // Directly change the window location to the home page or any other authenticated route
      window.location.href = '/Home';
    } catch (error) {
      console.error('Error logging in:', error.message);
      setIsAuthenticated(false);
      setErrorMessage('Invalid email or password');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginDetails(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <>
    <nav>
         <div class="logo">
            Edu Sphere
         </div>
         <input type="checkbox" id="click"/>
         <label for="click" class="menu-btn">
         <i class="fas fa-bars"></i>
         </label>
         <ul>
            <li><Link to="/Reg">Register</Link></li>
         </ul>
      </nav>

      <br /><br />
      <h3 className='su2'>Sign In</h3> <br />
      <p className="hr-line"></p>

      <ToastContainer />
      <div className="login-page">
        <div className="login-container">
          <h2 className="login-title">Sign In</h2>
          <form autoComplete="off">
            <div className="input-box">
              <i className="fa-solid fa-envelope"></i>
              <input
                type="text"
                placeholder="Enter your email"
                value={credentials.email}
                onChange={(e) => setCredentials({ ...credentials, email: e.target.value })}
              />
              {errors.email && <p className="error">{errors.email}</p>}
            </div>

            <div className="input-box">
              <i className="fa-solid fa-key"></i>
              <input
                type="password"
                placeholder="Enter your password"
                value={credentials.password}
                onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
              />
              {errors.password && <p className="error">{errors.password}</p>}
            </div>

            <div className="button">
              <input type="button" value="Sign In" onClick={handleLogin} />
            </div>
          </form>
          <p className="signup-link">
            Don't have an account? <Link to="/Register">Sign Up</Link>
          </p>
        </div>
      </div>

    </>

  );
}
