import React from 'react'
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';

export default function Home(){
    const [donors, setDonors] = useState([]);
    const [newDonor, setNewDonor] = useState({ id: '', name: '', age: '', amount: '', c_name: '' });
    const [fetchId, setFetchId] = useState('');
    const [fetchedDonor, setFetchedDonor] = useState(null);
    const [selectedId, setSelectedId] = useState(null);
    const [updatedDonor, setUpdatedDonor] = useState({ id: '', name: '', age: '', amount: '', c_name: '' });
    const [showNoDataMessage, setShowNoDataMessage] = useState(false);
  
    useEffect(() => {
      fetchDonors();
    }, []);
  
    const fetchDonors = () => {
      axios.get('http://localhost:5000/api/donors')
        .then(response => {
          setDonors(response.data);
        })
        .catch(error => console.error('Error fetching donors:', error));
    };
  
    const handleFetchData = () => {
      axios.get(`http://localhost:5000/api/donors/${fetchId}`)
        .then(response => {
          setFetchedDonor(response.data);
          setShowNoDataMessage(false);
        })
        .catch(error => {
          console.error('Error fetching donor:', error);
          setFetchedDonor(null);
          setShowNoDataMessage(true);
        });
    };
  
    const handleAddDonor = () => {
      axios.post('http://localhost:5000/api/donors', newDonor)
        .then(response => {
          fetchDonors();
          setNewDonor({ id: '', name: '', age: '', amount: '', c_name: '' });
        })
        .catch(error => console.error('Error adding donor:', error));
    };
  
    const handleUpdateDonor = (selectedId) => {
      axios.put(`http://localhost:5000/api/donors/${selectedId}`, updatedDonor)
        .then(response => {
          fetchDonors();
          setSelectedId(null);
          setUpdatedDonor({ id: '', name: '', age: '', amount: '', c_name: '' });
        })
        .catch(error => console.error('Error updating donor:', error));
    };
  
    const handleDeleteDonor = (id) => {
      axios.delete(`http://localhost:5000/api/donors/${id}`)
        .then(response => {
          fetchDonors();
          toast.success('donor removed successfully!');
        })
        .catch(error => {
          console.error('Error deleting donor:', error);
          toast.error('Error removing donation');
        });
    };
  
    return (
        <>
        <div className='responsive-container-block.bigContainer'>
        <div>
  <div class="carouseldon">
    <ul class="slides">
      <input type="radio" name="radio-buttons" id="img-1" checked />
      <li class="slide-container">
        <div class="slide-image">
          <img src="https://cache.careers360.mobi/media/article_images/2022/4/2/student_studying_in_dark_guy.jpg"/>
        </div>
        <div class="carousel-controls">
          <label for="img-3" class="prev-slide">
            <span>&lsaquo;</span>
          </label>
          <label for="img-2" class="next-slide">
            <span>&rsaquo;</span>
          </label>
        </div>
      </li>
      <input type="radio" name="radio-buttons" id="img-2" />
      <li class="slide-container">
        <div class="slide-image">
          <img src="https://content.r9cdn.net/rimg/dimg/db/02/06b291e8-city-14912-171317ad83a.jpg?width=1750&height=1000&xhint=3040&yhint=2553&crop=true"/>
        </div>
        <div class="carousel-controls">
          <label for="img-1" class="prev-slide">
            <span>&lsaquo;</span>
          </label>
          <label for="img-3" class="next-slide">
            <span>&rsaquo;</span>
          </label>
        </div>
      </li>
      <input type="radio" name="radio-buttons" id="img-3" />
      <li class="slide-container">
        <div class="slide-image">
          <img src="https://www.linearity.io/blog/content/images/2022/03/610963083219da6a0a00ccb6_Cover-Dark-2.png"/>
        </div>
        <div class="carousel-controls">
          <label for="img-2" class="prev-slide">
            <span>&lsaquo;</span>
          </label>
          <label for="img-1" class="next-slide">
            <span>&rsaquo;</span>
          </label>
        </div>
      </li>
      <div class="carousel-dots">
        <label for="img-1" class="carousel-dot" id="img-dot-1"></label>
        <label for="img-2" class="carousel-dot" id="img-dot-2"></label>
        <label for="img-3" class="carousel-dot" id="img-dot-3"></label>
      </div>
    </ul>
  </div>
</div>
</div>
<h1>this is crud operations</h1>
</>
    )
}
