import React, { useState } from 'react';
import { Routes, Route, Link } from 'react-router-dom';

export default function Login() {
  const [showPassword, setShowPassword] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };
  const [loginDetails, setLoginDetails] = useState({
    email: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: loginDetails.email,
          password: loginDetails.password,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Internal Server Error');
      }

      console.log('Login successful');

      // If login is successful, update isAuthenticated state
      setIsAuthenticated(true);

      // Directly change the window location to the home page or any other authenticated route
      window.location.href = '/Home';
    } catch (error) {
      console.error('Error logging in:', error.message);
      setIsAuthenticated(false);
      setErrorMessage('Invalid email or password');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginDetails(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <>
    <nav>
         <div class="logo">
            Edu Sphere
         </div>
         <input type="checkbox" id="click"/>
         <label for="click" class="menu-btn">
         <i class="fas fa-bars"></i>
         </label>
         <ul>
            <li><Link to="/Reg">Register</Link></li>
         </ul>
      </nav>

      <br /><br />



      <br></br><br></br>
      <div className="bg-img">
      <div className="content">
        <header>Login Form</header>
        <form onSubmit={handleLogin}>
          <div className="field">
            <span className="fa fa-user"></span>
            <input type="text" required placeholder="Enter email" name="email" value={loginDetails.email} onChange={handleInputChange}  />
          </div>
          <div className="field space">
            <span className="fa fa-lock"></span>
            <input
              type={showPassword ? "text" : "password"}
              className="pass-key"
              required
              placeholder="Enter Password"
			  value={loginDetails.password} onChange={handleInputChange} 
            />
            <span className="show" onClick={togglePasswordVisibility}>
              {showPassword ? "HIDE" : "SHOW"}
            </span>
          </div>
          <div className="pass">
            <a href="#">Forgot Password?</a>
          </div>
          <div className="field">
            <input type="submit" value="LOGIN" />
          </div>
		  {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </form>
        <div className="login">Or login with</div>
        <div className="links">
          <div className="facebook">
            <i className="fab fa-facebook-f">
              <span>Facebook</span>
            </i>
          </div>
          <div className="instagram">
            <i className="fab fa-instagram">
              <span>Instagram</span>
            </i>
          </div>
        </div>
        <div className="signup">
          Don't have an account?
          <a href="/Reg">Register Now</a>
        </div>
      </div>
    </div>

    </>
  );
}
