import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { FaCaretDown } from 'react-icons/fa';
import './sty.css';

export default function Marks() {
  const navigate = useNavigate();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [userName, setUserName] = useState('');
  const [studentId, setStudentId] = useState('');
  const [markById, setMarkById] = useState(null);
  const [newMark, setNewMark] = useState({
    student_id: '',
    telugu: '',
    english: '',
    science: '',
    maths: '',
    social: '',
    exam_type: ''
  });

  useEffect(() => {
    // Fetch user data when the component mounts
    const fetchUserName = async () => {
      try {
        const userId = localStorage.getItem('userId');
        const response = await axios.get(`http://localhost:3001/users/${userId}`);
        setUserName(response.data.name);
      } catch (error) {
        console.error('Error fetching user name:', error);
      }
    };

    fetchUserName();
  }, []);

  const handleLogout = () => {
    // Clear user data from local storage or any authentication tokens
    localStorage.removeItem('userRole');
    localStorage.removeItem('userId');
    // Redirect to login page
    navigate('/Login');
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const getMarkById = () => {
    axios.get(`http://localhost:3001/marks/${studentId}`)
      .then(response => {
        const fetchedMark = response.data;
        setMarkById(fetchedMark);
      })
      .catch(error => {
        console.error('Error fetching mark:', error);
        setMarkById(null);
      });
  };

  const validateMark = (value) => {
    const num = Number(value);
    return !isNaN(num) && num >= 0 && num <= 100;
  };

  const handleChange = (e, field) => {
    const { value } = e.target;
    if (validateMark(value) || value === '') {
      setNewMark({ ...newMark, [field]: value });
    }
  };

  const addNewMark = () => {
    // Logic to add the new mark goes here
    axios.post('http://localhost:3001/marks', newMark)
      .then(response => {
        console.log('Mark added successfully:', response.data);
        // Reset the form
        setNewMark({
          student_id: '',
          telugu: '',
          english: '',
          science: '',
          maths: '',
          social: '',
          exam_type: ''
        });
      })
      .catch(error => {
        console.error('Error adding mark:', error);
      });
  };

  return (
    <>
      <nav>
        <div className="logo">
          Edu Sphere
        </div>
        <input type="checkbox" id="click" />
        <label htmlFor="click" className="menu-btn">
          <i className="fas fa-bars"></i>
        </label>
        <ul>
          <li><Link to="/Home">Home</Link></li>
          <li><Link to="/About">About</Link></li>
          <li><Link to="/Marks">Marks</Link></li>
          <li><Link to="/Stumarks">My Marks</Link></li>
          <li><Link to="/Gallery">Gallery</Link></li>
          <li className="dropdown-icon" onClick={toggleDropdown}>
            <FaCaretDown size={30} />
            {dropdownOpen && (
              <div className="dropdown-menu">
                <p>Welcome, {userName}</p>
                <button onClick={handleLogout}>Logout</button>
              </div>
            )}
          </li>
        </ul>
      </nav>

      <div className="container">
        <table id="containertable1">
          <thead>
            <tr>
              <th><h3>Add New Mark</h3></th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                <input
                  type="text"
                  placeholder="ID"
                  value={newMark.student_id}
                  onChange={(e) => setNewMark({ ...newMark, student_id: e.target.value })}
                />
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="number"
                  placeholder="Telugu"
                  value={newMark.telugu}
                  onChange={(e) => handleChange(e, 'telugu')}
                />
              </td>
              <td>
                <input
                  type="number"
                  placeholder="English"
                  value={newMark.english}
                  onChange={(e) => handleChange(e, 'english')}
                />
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="number"
                  placeholder="Science"
                  value={newMark.science}
                  onChange={(e) => handleChange(e, 'science')}
                />
              </td>
              <td>
                <input
                  type="number"
                  placeholder="Maths"
                  value={newMark.maths}
                  onChange={(e) => handleChange(e, 'maths')}
                />
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="number"
                  placeholder="Social"
                  value={newMark.social}
                  onChange={(e) => handleChange(e, 'social')}
                />
              </td>
              <td>
                <select
                  value={newMark.exam_type}
                  onChange={(e) => setNewMark({ ...newMark, exam_type: e.target.value })}
                >
                  <option value="">Select Exam Type</option>
                  <option value="mid">Mid</option>
                  <option value="final">Final</option>
                  <option value="advanced">Advanced</option>
                </select>
              </td>
            </tr>
            <tr>
              <td>
                <button onClick={addNewMark}>Add New Mark</button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </>
  );
}
